from django.forms import ModelFormfrom django import formsfrom .models import *class CreateCategoryForm(ModelForm):    class Meta:        model = Category        fields = ['author', 'title', 'description', 'logo']        labels = {            'author': '',            'title': '',            'logo': '',            'description': '',        }        widgets = {            'author': forms.TextInput(attrs={'class': 'form-control', 'placeholder': 'Enter author name'}),            'title': forms.TextInput(attrs={'class': 'form-control', 'placeholder': 'Enter title'}),            'description': forms.TextInput(attrs={'class': 'form-control', 'placeholder': 'Enter description'}),            'logo': forms.FileInput(attrs={'class': 'form-control', 'placeholder': 'choose'}),        }class CreateQuestionForm(ModelForm):    class Meta:        model = Question        fields = ['category', 'question', 'logo', 'time']        labels = {            'category': '',            'question': '',            'logo': '',            'time': '',        }        widgets = {            'category': forms.Select(attrs={'class': 'form-control'}),            'question': forms.TextInput(attrs={'class': 'form-control'}),            'logo': forms.FileInput(attrs={'class': 'form-control', 'placeholder': 'choose'}),            'time': forms.TextInput(attrs={'class': 'form-control'}),        }class OptionPageForm(ModelForm):    class Meta:        model = Option        fields = ['answer', 'is_correct']        labels = {            'answer': '',            'is_correct': '',        }        widgets = {            'answer': forms.TextInput(attrs={'class': 'form-control', 'placeholder': ''}),            'is_correct': forms.CheckboxInput(attrs={'class': 'form-check-input', 'type': 'checkbox'}),        }